const out = [];

// Step 1: Collect all Google Sheet keywords
const sheetKeywords = items
  .filter(i => i.json.Targetted_Keywords) // from Sheets
  .map(i => (i.json.Targetted_Keywords || "").toLowerCase().trim());

// Deduplicate
const uniqueSheetKeywords = [...new Set(sheetKeywords)];

// Step 2: Collect all GSC keywords
const gscData = items.filter(i => i.json.keyword);

// Step 3: Match - GSC keywords found in sheet
const foundKeywords = new Set();

for (const item of gscData) {
  const gscKeyword = (item.json.keyword || "").toLowerCase().trim();

  if (uniqueSheetKeywords.includes(gscKeyword)) {
    foundKeywords.add(gscKeyword); // Track found keywords

    // Make sure URLs array includes CTR for each URL
    const urlsWithCtr = (item.json.urls || []).map(urlObj => ({
      url: urlObj.url,
      position: urlObj.position,
      clicks: urlObj.clicks,
      impressions: urlObj.impressions,
      ctr: urlObj.ctr  // CTR included here
    }));

    out.push({
      json: {
        keyword: item.json.keyword,
        urls: urlsWithCtr,
        status: 'found_in_gsc' // Optional: to identify matched keywords
      }
    });
  }
}

// Step 4: Add sheet keywords that were NOT found in GSC
for (const sheetKeyword of uniqueSheetKeywords) {
  if (!foundKeywords.has(sheetKeyword)) {
    out.push({
      json: {
        keyword: sheetKeyword,
        urls: [], // No URLs since not found in GSC
        status: 'not_found_in_gsc' // Optional: to identify missing keywords
      }
    });
  }
}

return out;
